import axios from "axios";

export const handleLoginSubmit = async (values) => {
  try {
    const response = await axios.post("/api/login", values);
    return response.data; // Return response to handle in Login component
  } catch (error) {
    console.error("Error logging in:", error);
    return { success: false };
  }
};

export const handleSignupSubmit = async (values) => {
  try {
    const response = await axios.post("/api/signup", values);
    return response.data; // Return response to handle in Signup component
  } catch (error) {
    console.error("Error signing up:", error);
    return { success: false };
  }
};
