import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useCredits } from '../utils/creditContext';
import '../styles/styles.css';
import '../styles/mediaQuery.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const Navbar = () => {
    const [isActive, setIsActive] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false); // Manage login state
    const [userCredits, setUserCredits] = useState(null); // User-specific credits
    const { credits } = useCredits(); // Assuming useCredits is used for fetching credits

    const handleMenuClick = () => {
        setIsActive(!isActive);
    };

    useEffect(() => {
        // Fetch user authentication status and credits here
        // This is just a placeholder for actual authentication check
        const fetchUserData = async () => {
            // Simulate user authentication
            const userLoggedIn = true; // Replace with actual authentication check
            setIsLoggedIn(userLoggedIn);

            if (userLoggedIn) {
                // Fetch user-specific credits
                const userCredits = await getUserCredits(); // Replace with actual API call
                setUserCredits(userCredits);
            }
        };

        fetchUserData();
    }, []);

    // Simulate fetching user credits
    const getUserCredits = async () => {
        // Simulate API call to fetch user credits
        return 50; // Replace with actual fetched credits
    };

    return (
        <nav className="navbar">
            <div className="navbar_container">
                <Link to="/" id="navbar_logo">
                    <i className="fa-solid fa-gem"></i>MICROTASKS
                </Link>
                <div
                    className={`navbar_toggle ${isActive ? 'is-active' : ''}`}
                    id="mobile-menu"
                    onClick={handleMenuClick}
                >
                    <span className="bar"></span>
                    <span className="bar"></span>
                    <span className="bar"></span>
                </div>
                <ul className={`navbar_menu ${isActive ? 'active' : ''}`}>
                    <li className="navbar_item">
                        <Link to="/" className="navbar_links">Home</Link>
                    </li>
                    <li className="navbar_item">
                        <Link to="/tasks" className="navbar_links">Tasks</Link>
                    </li>
                    {isLoggedIn && userCredits !== null && (
                        <li className="navbar_item">
                            <span className="credits_text">Credits: {userCredits}</span>
                        </li>
                    )}
                    <li id="lgn_btn" className="navbar_item">
                        <Link to="/login" className="button">Login</Link>
                    </li>
                    <li id="sgn_btn" className="navbar_item">
                        <Link to="/signup" className="button">Sign Up</Link>
                    </li>
                </ul>
            </div>
        </nav>
    );
};

export default Navbar;
